import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Slider = () => {
    const [data, setData] = useState(null);
    const api_key = process.env.STOCK_REACT_API_KEY;

    useEffect(() => {
        const fetchData = async () => {
            try {
                const options = {
                    method: 'GET',
                    url: 'https://yfapi.net/v6/finance/quote/marketSummary',
                    headers: {
                        'x-api-key': api_key
                    }
                };
                const response = await axios.request(options);
                setData(response.data.marketSummaryResponse.result);
            } catch (error) {
                console.error(error);
            }
        };
        fetchData();
    }, [api_key]);

    return (
        <>
            {data ? (
                <div className='slider'>
                    <div className='slider-container'>
                        {data.map((item, index) => (
                            <div className='slider-item' key={index}>
                                <span className='slider-name'>{item.shortName}</span>
                                {item.regularMarketPrice.fmt}
                                <span
                                    className='slider-change'
                                    style={{ color: item.regularMarketChange.raw > 0 ? 'green' : 'red' }}
                                >
                                    {item.regularMarketChange.raw > 0 ? `+${item.regularMarketChange.fmt}` : item.regularMarketChange.fmt}
                                    {` (${item.regularMarketChangePercent.fmt})`}
                                </span>
                            </div>
                        ))}
                    </div>
                </div>
            ) : (
                <div>Loading...</div>
            )}
        </>
    );
};

export default Slider;
